document.addEventListener('turbolinks:load', function () {
    var password = document.querySelector('.check-password')
    var passwordComfirm = document.querySelector('.confirm-password')

    if (password && passwordComfirm) { passwordComfirm.addEventListener('input', passwordCheck()); }

});

function passwordCheck() {
  var password = document.querySelector('.check-password')
  var passwordComfirm = document.querySelector('.confirm-password')
  console.log(str(password.value))
  console.log('passwordComfirm.value')

  if(password.classList.contains('border-red')){
    password.classList.remove('border-red')
  }

  if(password.classList.contains('border-green'))
    password.classList.remove('border-green')

  if(passwordComfirm.value === ''){
    document.querySelector('.text-success').classList.add('hide')
    document.querySelector('.text-danger').classList.add('hide')
    return
  } else if (password.value === passwordComfirm.value) {
      passwordComfirm.classList.add('text-success')
      showSuccessIcon()
  } else {
      passwordComfirm.classList.add('text-danger')
      showDangerIcon()
  }
}

function showSuccessIcon() { 
  if (document.querySelector('.text-success').classList.contains('hide')) {
    document.querySelector('.text-success').classList.remove('hide')
    document.querySelector('.text-danger').classList.add('hide')
  }
}

function showDangerIcon() {
  if (document.querySelector('.text-danger').classList.contains('hide')) {
    document.querySelector('.text-danger').classList.remove('hide')
    document.querySelector('.text-success').classList.add('hide')
  }
}
